function [prediction, net_t, o] = feedforward(x, Theta1, Theta2, a)  % Assumes X has already been enriched with the 1's column  a1 = x'; % the i-th example, transposed to get a column vector  netHidden = Theta1*a1; % the net vector of the HIDDEN units  a2 = sigmoid(a, netHidden);  a2 = [1; a2]; % add bias  o = a2;  net_t = (Theta2*a2); % the net of the t-th OUT unit     prediction = sigmoid(a, net_t);end