function myPlotWithTest(TR_Err, TR_Acc, nIter, TS_Err, TS_Acc, lossType, alpha, eta, lambda, layers_dims, out_dim, path, testName)  subplot(2,2,1);   plot(1:200, TR_Acc(1:200), 'marker', 'none', 'color', 'red');    %test set  hold on;  plot(1:200, TS_Acc(1:200), 'marker', 'none', 'linestyle', '--');  hold off;    %legend ('TR set', 'TS set');  %legend('boxoff');     title(strcat('Estimation per Epoch'));  xlabel('Epochs');  ylabel('MEE');  set (gca, 'ygrid', 'on');  set (gca, 'xgrid', 'on');      subplot(2,2,2);  plot(1:200, TR_Err(1:200), 'marker', 'none', 'color', 'red');    % TS Set  hold on;  plot(1:200, TS_Err(1:200), 'marker', 'none', 'linestyle', '--');  hold off;  %legend ('TR set', 'TS set');  %legend('boxoff');       title(strcat('Error per Epoch'));  xlabel('Epochs');  ylabel('MSE');  set (gca, 'ygrid', 'on');  set (gca, 'xgrid', 'on');    subplot(2,2,3);  plot(200:nIter, TR_Acc(200:nIter), 'marker', 'none', 'color', 'red');  hold on;  plot(200:nIter, TS_Acc(200:nIter), 'marker', 'none', 'linestyle', '--');  hold off;  xlabel('Epochs');  ylabel('MEE');  set (gca, 'ygrid', 'on');  set (gca, 'xgrid', 'on');    subplot(2,2,4);      plot(200:nIter, TR_Err(200:nIter), 'marker', 'none', 'color', 'red');  hold on;  plot(200:nIter, TS_Err(200:nIter), 'marker', 'none', 'linestyle', '--');  hold off;  xlabel('Epochs');  ylabel('MSE');  set (gca, 'ygrid', 'on');  set (gca, 'xgrid', 'on');      directoryName = path;  paramTitleUnderscore = strcat('loss_', lossType, ',eta_', num2str(eta), ',lambda_', num2str(lambda), ',alpha_', num2str(alpha), ',layers_', num2str(layers_dims));  filename = strcat(directoryName, testName, '_', paramTitleUnderscore, '.png');     print(filename, '-dpng');end  