function logPlotWithTest(TR_Err, TR_Acc, nIter, TS_Err, TS_Acc, lossType, alpha, eta, lambda, layers_dims, out_dim, directoryName, fold)  if nargin < 13      fold = 0;  end   if (fold ~= 0)    fold = strcat('_fold=', num2str(fold));  else     fold = '';  end    myPlotWithTest(TR_Err, TR_Acc, nIter, TS_Err, TS_Acc, lossType, alpha, eta, lambda, layers_dims, out_dim, directoryName, fold);end