function myPlot(TR_Err, TR_Acc, nIter, lossType, alpha, eta, lambda, layers_dims, out_dim, path, testName)  set(0, 'defaultaxesfontsize', 18);  %set(0, 'Position', [100,100,1080,1920]);  #figure('Position',[800,-800,1920, 1500]);  subplot(2,1,1);%, "position", [1,100,1,1]);   plot(1:nIter, TR_Acc(1:nIter));    #legend ("TR set");  #legend("boxoff");     _eta    = ["eta=", num2str(eta)];  _lambda = ["lambda=", num2str(lambda)];  _alpha  = ["alpha=", num2str(alpha)];  _layers = ["layers=", num2str(layers_dims)];  _out    = ["out neurons=", num2str(out_dim)];  paramTitle = strcat("loss=", lossType, ",eta=", num2str(eta), ",lambda=", num2str(lambda), ",alpha=", num2str(alpha));  %10 is newline ascii number  %legend([_eta,10,_lambda,10,_alpha,10,_layers,10,_out], 'location', 'southeast');  %legend("boxoff");       title(strcat("Estimation per Epoch:", paramTitle), "fontsize", 24);  xlabel("Epochs");  ylabel("Estimation (%)");  set (gca, "ygrid", "on");  set (gca, "xgrid", "on");    #axis("square");    subplot(2,1,2);%, "position", [0,1,1,1]);  plot(1:nIter, TR_Err(1:nIter));    #legend ("TR set");  #legend("boxoff");       title(strcat("Error per Epoch:", paramTitle), "fontsize", 24);  printf("Training error on the whole dataset: %f \n", TR_Err(nIter));  xlabel("Epochs");  ylabel("Error");  set (gca, "ygrid", "on");  set (gca, "xgrid", "on");  #axis("square");      directoryName = path;  paramTitleUnderscore = strcat("loss_", lossType, ",eta_", num2str(eta), ",lambda_", num2str(lambda), ",alpha_", num2str(alpha), ",layers_", num2str(layers_dims));  filename = strcat(directoryName, testName, "_", paramTitleUnderscore, ".png");     print(filename, "-dpng", "-S800,600");end  