function [f,g] = chooseHidden(hiddenActivationType)  if(strcmp(hiddenActivationType, "sigmoid") == 1)    f = @(z)(sigmoid(z));    g = @(z)(sigmoidGradient(z));  elseif(strcmp(hiddenActivationType, "tanh") == 1)    f = @(z)(tanh(z));    g = @(z)(tanhGradient(z));  else    printf("Hidden type not regognized: using sigmoid...\n");      f = @(z)(sigmoid(z));    g = @(z)(sigmoidGradient(z));  endifend