function [directoryName] = startLogging(testType)      logConfig;     newDir = "";    %if i'm doing a CV test  if (testType == CROSS_VL)      newDir = strcat("CV_", testName);  %if i'm doing a hold out  elseif (testType == HOLD_OUT)    newDir = strcat("HO_", testName);  %im doing a single run  else    newDir = strcat("ST_", testName);  endif    directoryName = strcat(logPath, "/", newDir, "/");    if (!isdir(directoryName))    mkdir(logPath, newDir);  endif    diary(strcat(directoryName, logName));  end   